function setup(){myCanvas=createCanvas(windowWidth,windowHeight),myCanvas.parent("#paint"),smooth(),num=800,colores()}function draw(){glitch()}function glitch(){noFill(),noiseSeed(ns);for(var n=0;n<num;n++){var o=TWO_PI/num*n;push(),translate(width/2,height/2),rotate(o),stroke(r,g,b,5);var i=500*noise(3*sin(o),1*cos(o));rect(0,10,i,4),pop()}}function mouseMoved(){thing()}function thing(){rotate(TWO_PI),noStroke(),fill(random(255),random(111),random(55),55)}function windowResized(){resizeCanvas(windowWidth,windowHeight)}function colores(){r=random(255),g=random(111),b=random(55)}var r,g,b,i,num,ns;